function removeBotaoAtivo(){var t=document.querySelectorAll("[data-tab-button]");for(i=0;i<t.length;i++)t[i].classList.remove("shows__tabs__button--is-active")}function escondeAsAbas(){var t=document.querySelectorAll("[data-tab-id]");for(i=0;i<t.length;i++)t[i].classList.remove("shows__list--is-active")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]"),e=document.getElementById("icon"),o=document.getElementById("close");for(i=0;i<t.length;i++)t[i].addEventListener("click",function(t){var e=t.target.dataset.tabButton,e=document.querySelector(`[data-tab-id=${e}]`);escondeAsAbas(),e.classList.add("shows__list--is-active"),removeBotaoAtivo(),t.target.classList.add("shows__tabs__button--is-active")});e.addEventListener("click",function(){document.querySelector(".header__content__mobile").classList.add("header__content__mobile--is-active")}),o.addEventListener("click",function(){document.querySelector(".header__content__mobile").classList.remove("header__content__mobile--is-active")})});